import React, { useState } from 'react';

const UsernameInput = ({ onUsernameSet }) => {
  const [username, setUsername] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    const trimmedUsername = username.trim();

    if (trimmedUsername && !isSubmitting) {
      setIsSubmitting(true);
      onUsernameSet(trimmedUsername);
    }
  };

  return (
    <div className="username-input-container">
      <div className="username-card">
        <div className="welcome-header">
          <h2>ðŸ’¬ Welcome to Chat App</h2>
          <p>Enter your name to start chatting with others</p>
        </div>

        <form onSubmit={handleSubmit} className="username-form">
          <div className="input-group">
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter your name..."
              className="username-input"
              autoFocus
              disabled={isSubmitting}
              maxLength={50}
            />
            <button 
              type="submit" 
              disabled={!username.trim() || isSubmitting}
              className="join-button"
            >
              {isSubmitting ? 'Joining...' : 'Join Chat'}
            </button>
          </div>
        </form>

        <div className="info-text">
          <p>
            <strong>ðŸ’¡ Tip:</strong> Open this page in multiple tabs or browsers to chat with yourself, 
            or share the URL with others on the same network!
          </p>
        </div>
      </div>
    </div>
  );
};

export default UsernameInput;
