import React, { useState, useEffect } from 'react';
import './App.css';
import UserSelector from './components/UserSelector';
import MessageList from './components/MessageList';
import MessageInput from './components/MessageInput';
import { getCurrentUser, addMessage, getConversation } from './utils/localStorage';

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [chatPartner, setChatPartner] = useState(null);
  const [messages, setMessages] = useState([]);
  const [showUserSelector, setShowUserSelector] = useState(true);

  useEffect(() => {
    const user = getCurrentUser();
    if (user) {
      setCurrentUser(user);
    }
  }, []);

  useEffect(() => {
    if (currentUser && chatPartner) {
      const conversation = getConversation(currentUser.id, chatPartner.id);
      setMessages(conversation);
    } else {
      setMessages([]);
    }
  }, [currentUser, chatPartner]);

  const handleCurrentUserChange = (user) => {
    setCurrentUser(user);
    setChatPartner(null); // Reset chat partner when current user changes
  };

  const handleUserSelect = (user) => {
    setChatPartner(user);
    setShowUserSelector(false);
  };

  const handleSendMessage = (messageData) => {
    const newMessage = addMessage(messageData);
    setMessages(prev => [...prev, newMessage]);
  };

  const handleBackToUserSelector = () => {
    setShowUserSelector(true);
    setChatPartner(null);
  };

  return (
    <div className="app">
      <div className="app-header">
        <h1>üí¨ Chat App</h1>
        {!showUserSelector && chatPartner && (
          <button 
            className="back-button"
            onClick={handleBackToUserSelector}
          >
            ‚Üê Back to Users
          </button>
        )}
      </div>

      <div className="app-content">
        {showUserSelector ? (
          <UserSelector 
            onUserSelect={handleUserSelect}
            onCurrentUserChange={handleCurrentUserChange}
          />
        ) : (
          <div className="chat-container">
            <MessageList 
              messages={messages}
              currentUser={currentUser}
              chatPartner={chatPartner}
            />
            <MessageInput 
              onSendMessage={handleSendMessage}
              currentUser={currentUser}
              chatPartner={chatPartner}
            />
          </div>
        )}
      </div>

      {currentUser && (
        <div className="current-user-indicator">
          <img src={currentUser.avatar} alt={currentUser.name} />
          <span>Logged in as {currentUser.name}</span>
        </div>
      )}
    </div>
  );
}

export default App;
