import React, { useEffect, useRef } from 'react';

const MessageList = ({ messages, currentUser, chatPartner }) => {
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    if (date.toDateString() === today.toDateString()) {
      return 'Today';
    } else if (date.toDateString() === yesterday.toDateString()) {
      return 'Yesterday';
    } else {
      return date.toLocaleDateString();
    }
  };

  const groupMessagesByDate = (messages) => {
    const groups = {};
    messages.forEach(message => {
      const date = formatDate(message.timestamp);
      if (!groups[date]) {
        groups[date] = [];
      }
      groups[date].push(message);
    });
    return groups;
  };

  const messageGroups = groupMessagesByDate(messages);

  if (!currentUser || !chatPartner) {
    return (
      <div className="message-list empty">
        <div className="empty-state">
          <h3>Welcome to Chat App</h3>
          <p>Select your identity and a chat partner to start messaging!</p>
        </div>
      </div>
    );
  }

  if (messages.length === 0) {
    return (
      <div className="message-list empty">
        <div className="empty-state">
          <h3>Start a conversation with {chatPartner.name}</h3>
          <p>Send your first message below!</p>
        </div>
      </div>
    );
  }

  return (
    <div className="message-list">
      <div className="chat-header">
        <img src={chatPartner.avatar} alt={chatPartner.name} className="chat-partner-avatar" />
        <div className="chat-partner-info">
          <h3>{chatPartner.name}</h3>
          <span className="chat-status">Online</span>
        </div>
      </div>

      <div className="messages-container">
        {Object.entries(messageGroups).map(([date, dayMessages]) => (
          <div key={date} className="message-date-group">
            <div className="date-separator">
              <span>{date}</span>
            </div>
            {dayMessages.map(message => (
              <div 
                key={message.id} 
                className={`message ${message.senderId === currentUser.id ? 'sent' : 'received'}`}
              >
                <div className="message-content">
                  <p>{message.text}</p>
                  <span className="message-time">{formatTime(message.timestamp)}</span>
                </div>
              </div>
            ))}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
    </div>
  );
};

export default MessageList;
